@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: 0.25rem;
  --radius-md: 0.375rem;
  --radius-lg: 0.5rem;
  --radius-xl: 0.75rem;

  /* Yappy Brand Colors */
  --color-yappy-orange: #F77C22;
  --color-yappy-dark: #272525;
  --color-yappy-bg: #EAEBEC;
  --color-yappy-border: #A9A9A9;
  --color-yappy-card: #FFFFFF;

  --color-background: var(--color-yappy-bg);
  --color-foreground: var(--color-yappy-dark);

  --color-card: var(--color-yappy-card);
  --color-card-foreground: var(--color-yappy-dark);

  --color-popover: var(--color-yappy-card);
  --color-popover-foreground: var(--color-yappy-dark);

  --color-primary: var(--color-yappy-orange);
  --color-primary-foreground: #FFFFFF;

  --color-secondary: #F4F4F5;
  --color-secondary-foreground: var(--color-yappy-dark);

  --color-muted: #F4F4F5;
  --color-muted-foreground: #71717A;

  --color-accent: var(--color-yappy-orange);
  --color-accent-foreground: #FFFFFF;

  --color-destructive: hsl(0 84% 60%);
  --color-destructive-foreground: hsl(0 0% 98%);

  --color-border: #E2E8F0; /* Softer border than strict A9A9A9 for internal elements */
  --color-input: #E2E8F0;
  --color-ring: var(--color-yappy-orange);

  --color-chart-1: var(--color-yappy-orange);
  --color-chart-2: #272525;
  --color-chart-3: #A9A9A9;
  --color-chart-4: #EAEBEC;
  --color-chart-5: #FDBA74;

  --font-sans: 'Plus Jakarta Sans', sans-serif;

  --radius: 0.5rem;

  --shadow-soft: 0 4px 20px -2px rgba(0, 0, 0, 0.05);
  --shadow-card: 0 2px 8px -1px rgba(0, 0, 0, 0.04);
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply font-sans antialiased bg-background text-foreground;
    background-color: #EAEBEC; /* Explicitly set to Yappy bg */
  }
}

@layer utilities {
  .perspective-1000 {
    perspective: 1000px;
  }
  .transform-style-3d {
    transform-style: preserve-3d;
  }
  .backface-hidden {
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden;
  }
  .rotate-y-180 {
    transform: rotateY(180deg);
  }
}

